private async Task CompareTime()
{
    await Task.Yield();

    string stockSymbol = $"ABC";
    decimal price = 10;

    long gmt = await JsRuntime.InvokeAsync<long>("getTime");
    this.ClientDatetime = new DateTime(gmt);
